let arrOfAvatar = unShuffledArrOfAvatar
    .map(value => ({ value, sort: Math.random() }))
    .sort((a, b) => a.sort - b.sort)
    .map(({ value }) => value)



let avatarWrapper = document.querySelector('#avatarWrapper');
let avatarDiv;

let logoAvatar = document.querySelector('#avatarImg');

let avatarSaveBtn = document.querySelector('#saveBtn');
let avatarBackBtn = document.querySelector('#backBtn');

let uploadImage = document.querySelector('#uploadImg');



logoAvatar.addEventListener('click', () => {
    changeAvatarObserverVar(1);
    gsap.to('#avatarMainSection', {
        duration: 0.5,
        x: "-24vw"
    })

    bgAvatarImage.src = avatarImage.src;
})


avatarSaveBtn.addEventListener('click', () => {
    checkImageUrlCorrectOrNot(uploadImage.value);
})

avatarBackBtn.addEventListener('click', () => {
    changeAvatarObserverVar(0);
    gsap.to('#settingSection', {
        duration: 0.5,
        x: "+=24vw",
        borderRadius: "2vw",
    })

    gsap.to('#avatarSetting', {
        duration: 1,
        background: "rgb(18 28 39)"
    })

    gsap.to('.settingInnerDiv', {
        duration: 1,
        background: "rgb(18 28 39)",
    })
    
    gsap.to('#settingSection > hr', {
        duration: 1,
        borderImage: "linear-gradient(90deg, rgba(255, 255, 255, 0) 0%, rgb(25, 43, 52) 5%, rgb(25, 43, 52) 95%, rgba(255,255,255,0) 100%) 1",
    })
    
    gsap.to('#settingSection > :nth-child(2)', {
        duration: 1,
        borderImage: "linear-gradient(90deg, rgba(255, 255, 255, 0) 0%, rgb(151, 189, 196) 5%, rgb(151, 189, 196) 95%, rgba(255,255,255,0) 100%) 1",
    })

    gsap.to('#avatarMainSection', {
        duration: 0.5,
        x: 0,
        borderRadius: "2vw",
        backgroundColor: "rgb(18 28 39)"
    })
})


for(let avatar in arrOfAvatar){
    let newAvatarDiv = document.createElement('div');

    newAvatarDiv.innerHTML = `
        <img src= ${arrOfAvatar[avatar]} >
    `;

    avatarWrapper.appendChild(newAvatarDiv);
    newAvatarDiv.classList.add('prebuildAvatar');
    newAvatarDiv.classList.add('displayFlex');
    avatarDiv = document.querySelectorAll('.prebuildAvatar');
}

avatarDiv.forEach((element) => {
    element.addEventListener('mouseover', () => {
        avatarImage.src = element.children[0].src;
        bgAvatarImage.src = element.children[0].src;
    })

    element.addEventListener('mouseout', () => {
        avatarImage.src = myAvatar;
        bgAvatarImage.src = myAvatar;
    })

    element.addEventListener('click', () => {

        gsap.to('#avatarMainSection', {
            duration: 0.5,
            x: 0
        })

        gsap.to('#settingSection', {
            duration: 0.5,
            x: "+=24vw",
            borderRadius: "2vw",
        })
    
        gsap.to('#avatarSetting', {
            duration: 1,
            background: "rgb(18 28 39)"
        })
    
        gsap.to('.settingInnerDiv', {
            duration: 1,
            background: "rgb(18 28 39)",
        })
        
        gsap.to('#settingSection > hr', {
            duration: 1,
            borderImage: "linear-gradient(90deg, rgba(255, 255, 255, 0) 0%, rgb(25, 43, 52) 5%, rgb(25, 43, 52) 95%, rgba(255,255,255,0) 100%) 1",
        })
        
        gsap.to('#settingSection > :nth-child(2)', {
            duration: 1,
            borderImage: "linear-gradient(90deg, rgba(255, 255, 255, 0) 0%, rgb(151, 189, 196) 5%, rgb(151, 189, 196) 95%, rgba(255,255,255,0) 100%) 1",
        })
    
        gsap.to('#avatarMainSection', {
            duration: 0.5,
            x: 0,
            borderRadius: "2vw",
            backgroundColor: "rgb(18 28 39)"
        })

        changeUserProfile(element.children[0].src);
    })
})